INSERT INTO `bh_knowled_standard` VALUES ('94', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '标准抗拉强度下限σb≥540MPa钢制压力容器，耐压试验后应当进行表面无损检测抽查', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('95', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '渗透方法的选用，首先应满足检测缺陷类型和灵敏度的要求，在此基础上，可根据被检工件表面粗糙度、检测批量大小和检测现场的水源、电源等条件来决定', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('96', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对于表面光洁且检测灵敏度要求高的工件，宜采用后乳化型做色法或后乳化型荧光法，也可采用溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('97', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对于表面粗糙且检测灵敏度要求低的工件宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('98', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对现场无水源、电源的检测宜采用溶剂去除型着色法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('99', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对于批量大的工件检测，宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('100', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对于大工件的局部检测，宜采用溶剂去除型着色法或溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('101', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '荧光法比着色法有较高的检测灵敏度', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('102', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '除非另有规定，焊接接头的渗透检测应在焊接完工后或焊接工序完成后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('103', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对有延迟裂纹倾向的材料，至少应在焊接完成24h后进行焊接接头的渗透检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('104', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '紧固件和锻件的渗透检测一般应安排在最终热处理之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('105', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对在用承压设备进行渗透检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('106', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '对在用承压设备进行渗透检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('107', '1', '板式换热器', '21', '板片', null, '渗透检测', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('108', '1', '板式换热器', '21', '板片', null, '金相检验', null, '有晶间腐蚀倾向的，可以采用金相检验检', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('109', '1', '板式换热器', '21', '板片', null, '金相检验', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('110', '1', '板式换热器', '21', '板片', null, '硬度检查', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('111', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '对无法进行内部检查的压力容器，应当采用可靠检测技术（例如内窥镜、声发射、超声检测等）从外部检测内表面缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('112', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '通常用于确定内部或表面存在的活性缺陷的强度和大致位置', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('113', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '适用于对承压设备在加载过程中进行的局部或整体检测，可用于在线监测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('114', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('115', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('116', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('117', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('118', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
