INSERT INTO `bh_knowled_standard` VALUES ('120', '1', '板式换热器', '21', '板片', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
