INSERT INTO `bh_knowled_standard` VALUES ('274', '3', '卧式分离器', '51', '筒体', null, '宏观检查', null, '有金属衬里的压力容器，如果发现衬里有穿透性腐蚀、裂纹、凹陷、检查孔已流出介质，应当局部或者全部拆除衬里层，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
