INSERT INTO `bh_knowled_standard` VALUES ('357', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '在检测中发现裂纹，检验人员应当根据可能存在的潜在缺陷，确定扩大表面无损检测的比例', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('358', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '如果扩检中仍发现裂纹，则应当进行全部焊接接头的表面无损检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('359', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '内表面的焊接接头已有裂纹的部位，对其相应外表面的焊接接头应当进行抽查', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('360', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '如果内表面无法进行检测，可以在外表面采用其他方法进行检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('361', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对应力集中部位、变形部位、异种钢焊接部位、奥氏体不锈钢堆焊层、T型焊接接头、其他有怀疑的焊接接头，补焊区，工卡具焊迹、电弧损伤处和易产生裂纹部位，应当重点检查，对焊接裂纹敏感的材料，注意检查可能发生的焊趾部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('362', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '绕带式压力容器的钢带始、末端焊接接头，应当进行表面无损检测，不得有裂纹', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('363', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '标准抗拉强度下限σb≥540MPa钢制压力容器，耐压试验后应当进行表面无损检测抽查', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('364', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '渗透方法的选用，首先应满足检测缺陷类型和灵敏度的要求，在此基础上，可根据被检工件表面粗糙度、检测批量大小和检测现场的水源、电源等条件来决定', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('365', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对于表面光洁且检测灵敏度要求高的工件，宜采用后乳化型做色法或后乳化型荧光法，也可采用溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('366', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对于表面粗糙且检测灵敏度要求低的工件宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('367', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对现场无水源、电源的检测宜采用溶剂去除型着色法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('368', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对于批量大的工件检测，宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('369', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对于大工件的局部检测，宜采用溶剂去除型着色法或溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('370', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '荧光法比着色法有较高的检测灵敏度', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('371', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '除非另有规定，焊接接头的渗透检测应在焊接完工后或焊接工序完成后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('372', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对有延迟裂纹倾向的材料，至少应在焊接完成24h后进行焊接接头的渗透检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('373', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '紧固件和锻件的渗透检测一般应安排在最终热处理之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('374', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对在用承压设备进行渗透检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('375', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '对在用承压设备进行渗透检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('376', '3', '卧式分离器', '51', '筒体', null, '渗透检测', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('377', '3', '卧式分离器', '51', '筒体', null, '金相检验', null, '有晶间腐蚀倾向的，可以采用金相检验检', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('378', '3', '卧式分离器', '51', '筒体', null, '金相检验', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('379', '3', '卧式分离器', '51', '筒体', null, '硬度检查', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('380', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '对无法进行内部检查的压力容器，应当采用可靠检测技术（例如内窥镜、声发射、超声检测等）从外部检测内表面缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('381', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '通常用于确定内部或表面存在的活性缺陷的强度和大致位置', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('382', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '适用于对承压设备在加载过程中进行的局部或整体检测，可用于在线监测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('383', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('384', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('385', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('386', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('387', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('388', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(6)有衬里或者因结构原因不能进行内表面检查的外表面焊接接头； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('389', '3', '卧式分离器', '51', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('390', '3', '卧式分离器', '51', '筒体', null, '涡流检测', null, '主要用于导电金属材料制承压设备表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('391', '3', '卧式分离器', '51', '筒体', null, '涡流检测', null, '通常能确定金属表面及近表面缺陷的位置和相对尺寸', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('392', '3', '卧式分离器', '51', '筒体', null, '涡流检测', null, '适用于导电金属材料和焊接接头表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('393', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '压力容器的铭牌、漆色、标志及喷涂的使用证号码是否符合有关规定', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('394', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '压力容器的本体、接口（阀门、管路）部位、焊接接头等是否有裂纹、过热、泄漏、损伤等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('395', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '外表面有无腐蚀，有无异常结霜等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('396', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '保温层有无破损、脱落、潮湿、跑冷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('397', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '检漏孔、信号孔有无漏液、漏气，检漏孔是否畅通', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('398', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '压力容器与相邻管道或构建有无异常振动、响声或者相互摩擦', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('399', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '支撑或者支座有无损坏，基础有无下沉、倾斜、开裂，紧固螺栓是否齐全、完好', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('400', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '排放（疏水、排污）装置是否完好', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('401', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '运行期间是否有超压、超温、超量等现象', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('402', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '罐体有接地装置的，检查接地装置是否符合要求', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('403', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '安全状况等级为4级的压力容器的监控措施执行情况和有无异常情况', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
