INSERT INTO `bh_knowled_standard` VALUES ('422', '3', '卧式分离器', '52', '封头', null, '宏观检查', null, '对于非金属材料做衬里的，如果发现衬里破损，龟裂或者脱落，或者在运行中本体壁温出现异常，应当局部或者全部拆除衬里，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
