INSERT INTO `bh_knowled_standard` VALUES ('571', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况，可以不拆除保温层：(2)外部环境没有水进入或者跑冷', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('572', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况，可以不拆除保温层：(3)对有代表性的部位进行抽查，未发现裂纹等缺陷', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('573', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况，可以不拆除保温层：(4)壁温在露点以上', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
