INSERT INTO `bh_knowled_standard` VALUES ('575', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '对外部绝热的容器及那些间歇适用的容器或在以下温度范围运行的容器，应考虑进行CUI检验：a.10℉(-12℃)～350℉(175℃)的碳钢和合金钢                ', '0', 'API510压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
