INSERT INTO `bh_knowled_standard` VALUES ('577', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：a.绝热层或加强圈', '0', 'API510压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('578', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：c.其他穿透部位，如悬梯挂钩、管支架', '0', 'API510压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
