INSERT INTO `bh_knowled_standard` VALUES ('581', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：f.顶部和底部封头', '0', 'API510压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('582', '2', '管壳式换热器', '12', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：g.其他容易积水的区域', '0', 'API510压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('583', '2', '管壳式换热器', '12', '筒体', null, '壁厚测定', null, '测定位置应当有代表性，有足够的测定点数', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('584', '2', '管壳式换热器', '12', '筒体', null, '壁厚测定', null, '测定后标图记录，对异常测厚点做详细标记', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
