INSERT INTO `bh_knowled_standard` VALUES ('586', '2', '管壳式换热器', '12', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(2)易受腐蚀、冲蚀的部位', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
