INSERT INTO `bh_knowled_standard` VALUES ('640', '2', '管壳式换热器', '12', '筒体', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光磁粉检测方法进行检测。', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('641', '2', '管壳式换热器', '12', '筒体', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光磁粉检测方法进行检测。', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
