INSERT INTO `bh_knowled_standard` VALUES ('677', '2', '管壳式换热器', '12', '筒体', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
