INSERT INTO `bh_knowled_standard` VALUES ('857', '2', '管壳式换热器', '14', '管箱', null, '壁厚测定', null, '当执行在线检验替代内部检验时，应采用超声波测厚或射线照相，或其他适当的无损检测方法测量金属厚度，彻底检查并评估金属和焊道的安全性', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('858', '2', '管壳式换热器', '14', '管箱', null, '壁厚测定', null, '必须进行许多有代表性的厚度测量。例如：所有主要部件（壳体、封头、锥形部分）以及具有代表性的容器接管都应测量和记录，并且应计算出部件的剩余寿命和下次检验间隔。', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('859', '2', '管壳式换热器', '14', '管箱', null, '壁厚测定', null, '对于局部腐蚀来说，采用能解释局部腐蚀的范围和程度的扫描法（诸如剖面射线照相、超声扫描以及其他适当的无损检测方法）进行的检验十分重要', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('860', '2', '管壳式换热器', '14', '管箱', null, '壁厚测定', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
