INSERT INTO `bh_knowled_standard` VALUES ('900', '2', '管壳式换热器', '14', '管箱', null, '磁粉检测', null, '对应力集中部位、变形部位、异种钢焊接部位、T型焊接接头、其他有怀疑的焊接接头，补焊区，工卡具焊迹、电弧损伤处和易产生裂纹部位，应当重点检查，对焊接裂纹敏感的材料，注意检查可能发生的焊趾部位', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('901', '2', '管壳式换热器', '14', '管箱', null, '磁粉检测', null, '绕带式压力容器的钢带始、末端焊接接头，应当进行表面无损检测，不得有裂纹', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
