INSERT INTO `bh_knowled_standard` VALUES ('903', '2', '管壳式换热器', '14', '管箱', null, '磁粉检测', null, '标准抗拉强度下限σb≥540MPa钢制压力容器，耐压试验后应当进行表面无损检测抽查', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('904', '2', '管壳式换热器', '14', '管箱', null, '磁粉检测', null, '焊接接头的磁粉检测应安排在焊接工序完成之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
