INSERT INTO `bh_knowled_standard` VALUES ('906', '2', '管壳式换热器', '14', '管箱', null, '磁粉检测', null, '除非另有要求，对于紧固件和锻件的磁粉检测应安排在最终热处理之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
