INSERT INTO `bh_knowled_standard` VALUES ('1198', '2', '管壳式换热器', '15', '封头', null, '磁粉检测', null, '焊接接头的磁粉检测应安排在焊接工序完成之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
