INSERT INTO `bh_knowled_standard` VALUES ('1200', '2', '管壳式换热器', '15', '封头', null, '磁粉检测', null, '除非另有要求，对于紧固件和锻件的磁粉检测应安排在最终热处理之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1201', '2', '管壳式换热器', '15', '封头', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光磁粉检测方法进行检测。', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1202', '2', '管壳式换热器', '15', '封头', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光磁粉检测方法进行检测。', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
