INSERT INTO `bh_knowled_standard` VALUES ('1220', '2', '管壳式换热器', '15', '封头', null, '渗透检测', null, '对于表面光洁且检测灵敏度要求高的工件，宜采用后乳化型做色法或后乳化型荧光法，也可采用溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
