INSERT INTO `bh_knowled_standard` VALUES ('1238', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：（1）使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1239', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1240', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1241', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1242', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1243', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(6)有衬里或者因结构原因不能进行内表面检查的外表面焊接接头； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1244', '2', '管壳式换热器', '15', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2004 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1245', '2', '管壳式换热器', '15', '封头', null, '涡流检测', null, '主要用于导电金属材料制承压设备表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1246', '2', '管壳式换热器', '15', '封头', null, '涡流检测', null, '通常能确定金属表面及近表面缺陷的位置和相对尺寸', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1247', '2', '管壳式换热器', '15', '封头', null, '涡流检测', null, '适用于导电金属材料和焊接接头表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1248', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '压力容器的铭牌、漆色、标志及喷涂的使用证号码是否符合有关规定', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1249', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '压力容器的本体、接口（阀门、管路）部位、焊接接头等是否有裂纹、过热、泄漏、损伤等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1250', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外表面有无腐蚀，有无异常结霜等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1251', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '保温层有无破损、脱落、潮湿、跑冷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1252', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '检漏孔、信号孔有无漏液、漏气，检漏孔是否畅通', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1253', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '压力容器与相邻管道或构建有无异常振动、响声或者相互摩擦', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1254', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '支撑或者支座有无损坏，基础有无下沉、倾斜、开裂，紧固螺栓是否齐全、完好', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1255', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '排放（疏水、排污）装置是否完好', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1256', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '运行期间是否有超压、超温、超量等现象', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1257', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '罐体有接地装置的，检查接地装置是否符合要求', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1258', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '安全状况等级为4级的压力容器的监控措施执行情况和有无异常情况', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1259', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '容器本体、对接焊缝、接管角焊缝等部位的裂纹、过热、泄漏等，焊缝表面（包括近缝区）以肉眼或者5-10倍放大镜检查裂纹', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1260', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '认真检验壳体和封头的表面，查找可能存在的裂纹、气孔、疤结及其他劣化现象', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1261', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '对容器裙座、支撑附件和封头的圆弧区域要特别注意', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1262', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '检查全部人孔、接管和其他开口表面有无变形、裂纹和其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1263', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '要特别注意施加于附加部件及其补强板上的焊道', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1264', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '内外表面的腐蚀和机械损伤', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1265', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '排放（疏水、排污）装置', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1266', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '检查焊道及邻近热影响区，查找由于运行产生的裂纹或其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1267', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '对铆接容器，要检查铆钉头、对接搭板、筒板、填缝边缘等的情况', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1268', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外部直观检验至少应确定壳体、封头、接管、外部隔热、支撑和结构部件、压力泄放装置、膨胀间隙以及容器与其支撑的水平度和垂直度', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1269', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '任何泄漏的迹象都应予以调查以查明泄漏源', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1270', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '如果整个容器壳体的温度保持在足以防止潮气凝结的低温或高温状态，则无需拆除隔热层', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1271', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外部检验的实施是为了检查容器外表面的状况、绝热系统、喷漆和涂层系统、支撑件、附属结构', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1272', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在外部检验中要特别注意连接件（如加强板、卡具）的焊缝是否具有开裂或其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1273', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '检查泄露、过热部位、振动、膨胀余量以及容器在其支撑件上的常规位置', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1274', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '保温层的破损、脱落、潮湿、跑冷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1275', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '有金属衬里的压力容器，如果发现衬里有穿透性腐蚀、裂纹、凹陷、检查孔已流出介质，应当局部或者全部拆除衬里层，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1276', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '带堆焊层的，堆焊层的龟裂、剥离和脱落等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1277', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '对于非金属材料做衬里的，如果发现衬里破损，龟裂或者脱落，或者在运行中本体壁温出现异常，应当局部或者全部拆除衬里，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1278', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(1)外表面有可靠的防腐蚀措施', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1279', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(2)外部环境没有水进入或者跑冷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1280', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(3)对有代表性的部位进行抽查，未发现裂纹等缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1281', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，由以下情况可以不拆除保温层：(4)壁温在露点以上', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1282', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，由以下情况可以不拆除保温层：(5)有类似的成功使用经验', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1283', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '对外部绝热的容器及那些间歇适用的容器或在以下温度范围运行的容器，应考虑进行CUI检验：a.10℉(-12℃)～350℉(175℃)的碳钢和合金钢                ', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1284', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '对外部绝热的容器及那些间歇适用的容器或在以下温度范围运行的容器，应考虑进行CUI检验：b.140℉(60℃)～400℉(205℃)的奥氏体不锈钢                       ', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1285', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：a.绝热层或加强圈', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1286', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：c.其他穿透部位，如悬梯挂钩、管支架', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1287', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：d.损坏的绝热层', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1288', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：e.堵缝失效的绝热层', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1289', '9', '过滤器', '61', '筒体', null, '宏观检查', null, '在制定CUI检验计划时，检验员宜考虑对CUI最敏感的那些区域。对压力容器来说，此类区域包括：g.其他容易积水的区域', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1290', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '测定位置应当有代表性，有足够的测定点数', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1291', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '测定后标图记录，对异常测厚点做详细标记', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1292', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(1)液位经常波动的部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1293', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(2)易受腐蚀、冲蚀的部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1294', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(3)制造成型时壁厚减薄部位和使用中易产生变形及磨损的部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1295', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(4)表面缺陷检查时，发现的可疑部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1296', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '壁厚测定时，如果遇母材夹层缺陷，应当增加测定点或者用超声检测，查明夹层分布情况以及与母材表面的倾斜度，同时作图记录', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1297', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '当执行在线检验替代内部检验时，应采用超声波测厚或射线照相，或其他适当的无损检测方法测量金属厚度，彻底检查并评估金属和焊道的安全性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1298', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '必须进行许多有代表性的厚度测量。例如：所有主要部件（壳体、封头、锥形部分）以及具有代表性的容器接管都应测量和记录，并且应计算出部件的剩余寿命和下次检验间隔', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1299', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '对于局部腐蚀来说，采用能解释局部腐蚀的范围和程度的扫描法（诸如剖面射线照相、超声扫描以及其他适当的无损检测方法）进行的检验十分重要', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1300', '9', '过滤器', '61', '筒体', null, '壁厚测定', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1301', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '主要用于承压设备的内部缺陷的检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1302', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '通常能确定缺陷的位置和相对尺寸', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1303', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '适用于板材、复合板材、碳钢和低合金钢锻件、管材、棒材、奥氏体不锈钢锻件等承压设备原材料和零部件的检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1304', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '也适用于承压设备对接焊接接头、T型焊接接头、角焊缝以及堆焊层等的检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1305', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1306', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1307', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1308', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1309', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1310', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(6)有衬里或者因结构原因不能进行内表面检查的外表面焊接接头； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1311', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1312', '9', '过滤器', '61', '筒体', null, '超声波检测', null, '对无法进行内部检查的压力容器，应当采用可靠检测技术（例如内窥镜、声发射、超声检测等）从外部检测内表面缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1313', '9', '过滤器', '61', '筒体', null, '射线检测', null, '主要用于承压设备的内部缺陷的检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1314', '9', '过滤器', '61', '筒体', null, '射线检测', null, '确定缺陷平面投影的位置、大小，可获得缺陷平面图形并能据此判定缺陷的性质', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1315', '9', '过滤器', '61', '筒体', null, '射线检测', null, '适用于金属材料制承压设备熔化焊对接焊接接头的检测，用于制作对接焊接接头的金属材料包括碳素钢、低合金钢、不锈钢、铜及铜合金、铝及铝合金、钛及钛合金、镍及镍合金', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1316', '9', '过滤器', '61', '筒体', null, '射线检测', null, '不适用于锻件、管材、棒材的检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1317', '9', '过滤器', '61', '筒体', null, '射线检测', null, 'T型焊接接头、角焊缝以及堆焊层的检测一般也不采用射线检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1318', '9', '过滤器', '61', '筒体', null, '射线检测', null, '除非另有规定，射线检测应在焊后进行', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1319', '9', '过滤器', '61', '筒体', null, '射线检测', null, '对有延迟裂纹倾向的材料，至少应在焊接完成24h后进行射线检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1320', '9', '过滤器', '61', '筒体', null, '射线检测', null, '如果怀疑铆钉钉体有腐蚀，可以进行锤击检查或对铆钉体部位进行与其轴线成一定角度的射线探伤', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1321', '9', '过滤器', '61', '筒体', null, '射线检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1322', '9', '过滤器', '61', '筒体', null, '射线检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
