INSERT INTO `bh_knowled_standard` VALUES ('1332', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '有下列情况的，对容器内表面对接焊缝进行磁粉或者渗透检测，检测长度不少于每条对接焊缝长度的20% ：(1)首次进行全面检验的第三类压力容器', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
