INSERT INTO `bh_knowled_standard` VALUES ('1336', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '在检测中发现裂纹，检验人员应当根据可能存在的潜在缺陷，确定扩大表面无损检测的比例', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
