INSERT INTO `bh_knowled_standard` VALUES ('1338', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '内表面的焊接接头已有裂纹的部位，对其相应外表面的焊接接头应当进行抽查', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1339', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '如果内表面无法进行检测，可以在外表面采用其他方法进行检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1340', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '对应力集中部位、变形部位、异种钢焊接部位、T型焊接接头、其他有怀疑的焊接接头，补焊区，工卡具焊迹、电弧损伤处和易产生裂纹部位，应当重点检查，对焊接裂纹敏感的材料，注意检查可能发生的焊趾部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1341', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '绕带式压力容器的钢带始、末端焊接接头，应当进行表面无损检测，不得有裂纹', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1342', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '铁磁性材料的表面无损检测优先选用磁粉检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1343', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '标准抗拉强度下限σb≥540MPa钢制压力容器，耐压试验后应当进行表面无损检测抽查', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1344', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '焊接接头的磁粉检测应安排在焊接工序完成之后进行', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
