INSERT INTO `bh_knowled_standard` VALUES ('1346', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '除非另有要求，对于紧固件和锻件的磁粉检测应安排在最终热处理之后进行', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1347', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光磁粉检测方法进行检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1348', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '对在用承压设备进行磁粉检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光磁粉检测方法进行检测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1349', '9', '过滤器', '61', '筒体', null, '磁粉检测', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
