INSERT INTO `bh_knowled_standard` VALUES ('1530', '9', '过滤器', '62', '封头', null, '声发射检测', null, '适用于对承压设备在加载过程中进行的局部或整体检测，可用于在线监测', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1531', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1532', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1533', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1534', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1535', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1536', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(6)有衬里或者因结构原因不能进行内表面检查的外表面焊接接头； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1537', '9', '过滤器', '62', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
