INSERT INTO `bh_knowled_standard` VALUES ('1563', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '如果整个容器壳体的温度保持在足以防止潮气凝结的低温或高温状态，则无需拆除隔热层', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
