INSERT INTO `bh_knowled_standard` VALUES ('1568', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '有金属衬里的压力容器，如果发现衬里有穿透性腐蚀、裂纹、凹陷、检查孔已流出介质，应当局部或者全部拆除衬里层，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1569', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '带堆焊层的，堆焊层的龟裂、剥离和脱落等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1570', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '对于非金属材料做衬里的，如果发现衬里破损，龟裂或者脱落，或者在运行中本体壁温出现异常，应当局部或者全部拆除衬里，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1571', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(1)外表面有可靠的防腐蚀措施', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1572', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(2)外部环境没有水进入或者跑冷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1573', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，有以下情况可以不拆除保温层：(3)对有代表性的部位进行抽查，未发现裂纹等缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1574', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，由以下情况可以不拆除保温层：(4)壁温在露点以上', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1575', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '外保温层一般应当拆除，拆除的部位、比例由检验人员确定，由以下情况可以不拆除保温层：(5)有类似的成功使用经验', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1576', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '对外部绝热的容器及那些间歇适用的容器或在以下温度范围运行的容器，应考虑进行CUI检验：a.10℉(-12℃)～350℉(175℃)的碳钢和合金钢                ', '0', 'API510-2014压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1577', '4', '立式罐', '41', '筒体', null, '宏观检查', null, '对外部绝热的容器及那些间歇适用的容器或在以下温度范围运行的容器，应考虑进行CUI检验：b.140℉(60℃)～400℉(205℃)的奥氏体不锈钢                       ', '0', 'API510-2014压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
