INSERT INTO `bh_knowled_standard` VALUES ('1587', '4', '立式罐', '41', '筒体', null, '壁厚测定', null, '厚度测定点的位置一般应当选择以下部位：(3)制造成型时壁厚减薄部位和使用中易产生变形及磨损的部位', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
