INSERT INTO `bh_knowled_standard` VALUES ('1847', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '对容器裙座、支撑附件和封头的圆弧区域要特别注意', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
