INSERT INTO `bh_knowled_standard` VALUES ('1856', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '如果整个容器壳体的温度保持在足以防止潮气凝结的低温或高温状态，则无需拆除隔热层', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1857', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '外部检验的实施是为了检查容器外表面的状况、绝热系统、喷漆和涂层系统、支撑件、附属结构', '0', 'API510-2014压力容器检验规范：在用检验、定级、修理和改造', null, null, null, null, null);
