INSERT INTO `bh_knowled_standard` VALUES ('1861', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '有金属衬里的压力容器，如果发现衬里有穿透性腐蚀、裂纹、凹陷、检查孔已流出介质，应当局部或者全部拆除衬里层，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1862', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '带堆焊层的，堆焊层的龟裂、剥离和脱落等', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('1863', '6', '卧式罐', '31', '筒体', null, '宏观检查', null, '对于非金属材料做衬里的，如果发现衬里破损，龟裂或者脱落，或者在运行中本体壁温出现异常，应当局部或者全部拆除衬里，查明本体的腐蚀状况或者其他缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
