INSERT INTO `bh_knowled_standard` VALUES ('1883', '6', '卧式罐', '31', '筒体', null, '壁厚测定', null, '当执行在线检验替代内部检验时，应采用超声波测厚或射线照相，或其他适当的无损检测方法测量金属厚度，彻底检查并评估金属和焊道的安全性', '0', 'SY/T 6507-2010压力容器检验规范维护检验、定级、修理和改造', null, null, null, null, null);
