INSERT INTO `bh_knowled_standard` VALUES ('1961', '6', '卧式罐', '31', '筒体', null, '渗透检测', null, '对在用承压设备进行渗透检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
