INSERT INTO `bh_knowled_standard` VALUES ('2056', '6', '卧式罐', '32', '封头', null, '射线检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
