INSERT INTO `bh_knowled_standard` VALUES ('2074', '6', '卧式罐', '32', '封头', null, '磁粉检测', null, '绕带式压力容器的钢带始、末端焊接接头，应当进行表面无损检测，不得有裂纹', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
