INSERT INTO `bh_knowled_standard` VALUES ('2099', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对于表面光洁且检测灵敏度要求高的工件，宜采用后乳化型做色法或后乳化型荧光法，也可采用溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2100', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对于表面粗糙且检测灵敏度要求低的工件宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2101', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对现场无水源、电源的检测宜采用溶剂去除型着色法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2102', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对于批量大的工件检测，宜采用水洗型着色法或水洗型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2103', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对于大工件的局部检测，宜采用溶剂去除型着色法或溶剂去除型荧光法', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2104', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '荧光法比着色法有较高的检测灵敏度', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2105', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '除非另有规定，焊接接头的渗透检测应在焊接完工后或焊接工序完成后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2106', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对有延迟裂纹倾向的材料，至少应在焊接完成24h后进行焊接接头的渗透检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2107', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '紧固件和锻件的渗透检测一般应安排在最终热处理之后进行', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2108', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对在用承压设备进行渗透检测时，如制造时采用高强度钢以及对裂纹（包括冷裂纹、热裂纹、再热裂纹）敏感的材料，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2109', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '对在用承压设备进行渗透检测时，长期工作在腐蚀介质环境下，有可能发生应力腐蚀裂纹的场合，其内壁宜采用荧光渗透检测方法进行检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2110', '6', '卧式罐', '32', '封头', null, '渗透检测', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2111', '6', '卧式罐', '32', '封头', null, '金相检验', null, '有晶间腐蚀倾向的，可以采用金相检验检', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2112', '6', '卧式罐', '32', '封头', null, '金相检验', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2113', '6', '卧式罐', '32', '封头', null, '硬度检查', null, '当目视检测发现异常情况，且不能判断缺陷的性质和影响时，可采用厚度测量、硬度测量、金相组织检验、磁粉或渗透检测等其他无损检测方法对异常处进行检测和评价', '0', 'JB4730.7-2011承压设备无损检测 第7部分：目视检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2114', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '对无法进行内部检查的压力容器，应当采用可靠检测技术（例如内窥镜、声发射、超声检测等）从外部检测内表面缺陷', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2115', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '通常用于确定内部或表面存在的活性缺陷的强度和大致位置', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2116', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '适用于对承压设备在加载过程中进行的局部或整体检测，可用于在线监测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2117', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(1)使用过程中补焊过的部位； 查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2118', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(2)检验时发现焊缝表面裂纹，认为需要进行焊缝缺陷检查的部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2119', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(3)错边量和棱角度超过制造标准要求的焊缝部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2120', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(4)使用中出现焊接接头泄漏的部位及其两端延长部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2121', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(5)承受交变载荷设备的焊接接头和其他应力集中部位；抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2122', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(6)有衬里或者因结构原因不能进行内表面检查的外表面焊接接头； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2123', '6', '卧式罐', '32', '封头', null, '声发射检测', null, '有以下情况时，应当进行射线检测或者超声检测抽查，必要时相互复验：(7)用户要求或者检验人员认为有必要的部位； 抽查比例或者是否采用其他检测方法复验，由检验人员根据具体情况确定；必要时，可以用声发射判断缺陷的活动性', '0', 'TSG R 7001-2013 压力容器定期检验规则', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2124', '6', '卧式罐', '32', '封头', null, '涡流检测', null, '主要用于导电金属材料制承压设备表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2125', '6', '卧式罐', '32', '封头', null, '涡流检测', null, '通常能确定金属表面及近表面缺陷的位置和相对尺寸', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2126', '6', '卧式罐', '32', '封头', null, '涡流检测', null, '适用于导电金属材料和焊接接头表面和近表面缺陷的检测', '0', 'JB4730.1~6-2005 承压设备无损检测', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2127', '8', '压力管线', '71', '直管', null, '宏观检查', null, '外观检验是确定管道、绝热层、防腐漆、涂层和与管道相连的构件的外表面状况，检查有无偏移、振动和泄漏', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2128', '8', '压力管线', '71', '直管', null, '宏观检查', null, '外部宏观检验用来确定管道外表面、绝热层、涂层、覆盖层以及相关金属构件的情况，并检查偏离、振动和泄漏的迹象', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2129', '8', '压力管线', '71', '直管', null, '宏观检查', null, '外部检验可在管道运行状态下进行', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2130', '8', '压力管线', '71', '直管', null, '宏观检查', null, '外部检验应包括管道支承和吊架的检验', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2131', '8', '压力管线', '71', '直管', null, '宏观检查', null, '腐蚀、冲蚀和结垢，将阀门、管件拆下或在法兰处拱起管子将其分开，以便外观检查', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2132', '8', '压力管线', '71', '直管', null, '宏观检查', null, '裂纹，除了强力组装焊道、焊缝附近的热影响区以及约束点和高应变区之外，焊缝包括角焊缝，易受到应力腐蚀开裂、氢损伤、碱脆或胺脆的位置，以及螺纹连接处的外露螺纹也要注意', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2133', '8', '压力管线', '71', '直管', null, '宏观检查', null, '焊接接头，若怀疑在焊道处发生腐蚀，检验方案中就应包括对焊道进行抽样检验', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2134', '8', '压力管线', '71', '直管', null, '宏观检查', null, '偏移度', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2135', '8', '压力管线', '71', '直管', null, '宏观检查', null, '振动：在操作中发现过度振动或摆动的地方，停车后应检查运行时无法查到的位置有无焊缝裂纹，并应检查其振动和外部摩擦情况', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2136', '8', '压力管线', '71', '直管', null, '宏观检查', null, '过热点：在操作过程中发现在装有内部隔热层的管道出现过热点的地方，停工后应对内部隔热层进行外观检查，看有无局部或完全脱落情况', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2137', '8', '压力管线', '71', '直管', null, '宏观检查', null, '应对过热点附近的管壁进行外观检查，看有无氧化和由此产生的氧化层', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2138', '8', '压力管线', '71', '直管', null, '宏观检查', null, '应清除全部氧化层，检查剩下的完好金属有无新出现的裂纹', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2139', '8', '压力管线', '71', '直管', null, '宏观检查', null, '若发现管道中一个区域有劣化，则应对该区域的上游和下游管道进行检验，还要检验相连的设备', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2140', '8', '压力管线', '71', '直管', null, '宏观检查', null, '若发现压力容器发生劣化，也应检验相连管道', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2141', '8', '压力管线', '71', '直管', null, '宏观检查', null, '复查绝热层的完好性，是否可以导致绝热层下腐蚀，绝热层下有无腐蚀迹象', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2142', '8', '压力管线', '71', '直管', null, '宏观检查', null, '应检查防腐衬里的脱落、破损、穿孔和鼓包情况', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2143', '8', '压力管线', '71', '直管', null, '宏观检查', null, '若发现衬里出现脱落、破损、穿孔或鼓包中任一情况，就有必要拆除部分内部衬里，检查衬里的耐腐蚀效力以及衬里下金属的表面状况', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2144', '8', '压力管线', '71', '直管', null, '宏观检查', null, '对于有耐火衬里类型的管道，应通过目测、红外线测量或温度指示涂料定期地进行温度监控，以确保衬里完好', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2145', '8', '压力管线', '71', '直管', null, '宏观检查', null, '如果发现耐火衬里出现脱离和鼓包，应拆除部分耐火衬里检查其下的管道', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2146', '8', '压力管线', '71', '直管', null, '宏观检查', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2147', '8', '压力管线', '71', '直管', null, '宏观检查', null, '当条件允许且实际需要时，可对大口径传输管道、排泄管道、催化剂管道或其他大口径管道等管道系统做内部外观检验', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2148', '8', '压力管线', '71', '直管', null, '厚度测定', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2149', '8', '压力管线', '71', '直管', null, '厚度测定', null, '如果未发现耐火衬里出现脱离和鼓包，应在金属外表面进行超声测厚', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2150', '8', '压力管线', '71', '直管', null, '厚度测定', null, '当外观检查的区域中发现有不规则的腐蚀或冲蚀时，最好用射线检测或超声波测厚仪检验一下管道中看不见的区域', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2151', '8', '压力管线', '71', '直管', null, '厚度测定', null, '在定期检验中，应对注入点管段的上游305m(12in)区域和下游至少十倍管径区域进行全面的检验，并测量和记录所有注入点管段上的定点测厚区的测厚值', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2152', '8', '压力管线', '71', '直管', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：a)在注入点管段上选取适当附件作为定点测厚区', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2153', '8', '压力管线', '71', '直管', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：b)选取受注入流体冲击的位置作为定点测厚区', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2154', '8', '压力管线', '71', '直管', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：c)在注入点管段的长直管段上选取中间部位作为定点测厚区域', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2155', '8', '压力管线', '71', '直管', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：d)在注入点管段上选取上游和下游的两端位置作为定点测厚区', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2156', '8', '压力管线', '71', '直管', null, '厚度测定', null, '检验员应测量所选择盲管的厚度，包括滞流末端和它与相邻管道的连接处', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2157', '8', '压力管线', '71', '直管', null, '厚度测定', null, '检验注入点较好的方法为射线检测法及超声波检测法，要适当的确定出每个定点测厚区的最小厚度值', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2158', '8', '压力管线', '71', '直管', null, '厚度测定', null, '对于容易产生局部冲蚀/腐蚀的部位，应采用适当的无损检测方法进行大面积测厚，如超声波检测、射线检测或涡流检测', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2159', '8', '压力管线', '71', '直管', null, '厚度测定', null, '当管道公称直径大于等于25mm时，用超声波测厚仪测厚通常是最精确测厚的方法', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2160', '8', '压力管线', '71', '直管', null, '厚度测定', null, '射线测厚技术更适合于管道公称直径为25mm和更小的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2161', '8', '压力管线', '71', '直管', null, '厚度测定', null, '对于局部位置测量可以使用射线测厚技术，特别是对有绝热层的或可能有不均匀腐蚀、局部腐蚀的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2162', '8', '压力管线', '71', '直管', null, '厚度测定', null, '如果可行，可采用超声波测量需要记录部位的实际厚度', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2163', '8', '压力管线', '71', '直管', null, '厚度测定', null, '当管道系统的腐蚀不均匀或剩余壁厚接近最小允许壁厚时，则需要进行附加测厚。', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2164', '8', '压力管线', '71', '直管', null, '厚度测定', null, '需要进行附加测厚时，采用射线或超声波扫描法更合适，也可使用涡流探伤设备', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2165', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：a) 发生泄漏事故后对安全和环境造成很大危害的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2166', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：b) 预计或经验证明腐蚀速率很高的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2167', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：c) 局部腐蚀严重的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2168', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：d) 结构上比较复杂的管道，如带有支管、盲管、连接件和其他类似的附件', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2169', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：e) 绝热层下严重腐蚀的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2170', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：a) 发生泄漏事故后对安全和环境危害不大的管道；', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2171', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：b) 相对无腐蚀的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2172', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：c) 长、直的管道。', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2173', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有下列特征之一的管道系统．可以不用测厚：a) 在泄漏时，不危险或对环境影响极小的管道', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2174', '8', '压力管线', '71', '直管', null, '厚度测定', null, '具有下列特征之一的管道系统．可以不用测厚：b) 经资料或类似工况证实为不腐蚀及腐蚀程度不影响管道安全运行。', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2175', '8', '压力管线', '71', '直管', null, '厚度测定', null, '每一个管道系统都应通过在测厚点进行厚度测量来监控', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2176', '8', '压力管线', '71', '直管', null, '厚度测定', null, '每个测厚点的最小壁厚值可用过超声波扫描或射线照相来确定', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2177', '8', '压力管线', '71', '直管', null, '厚度测定', null, '电磁技术也可用来确定减薄区域，然后用超声波或射线测量', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2178', '8', '压力管线', '71', '直管', null, '厚度测定', null, '采用超声波测厚时，应在测厚位置多测几次以查找局部减薄，并应记录检测点的最小厚度值或几次测量值的平均值，然后计算管道的腐蚀速率、剩余寿命及下次检验日期。', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2179', '8', '压力管线', '71', '直管', null, '超声波检测', null, '在运行时管道表面出现沉积物的部位，如结焦部位，确定这些沉积物下是否存在严重的腐蚀是非常重要的。对于小口径管道，可以拆除选定双端法兰管，或者采用如射线检测法或外部超声检测法等无损检测方法对该区域进行检测', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2180', '8', '压力管线', '71', '直管', null, '超声波检测', null, '在运行时管道表面出现沉积物的部位，如结焦部位，确定这些沉积物下是否存在严重的腐蚀是非常重要的。对于大口径管道，应清除选定区域的沉积物进行坑蚀检验', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2181', '8', '压力管线', '71', '直管', null, '超声波检测', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2182', '8', '压力管线', '71', '直管', null, '超声波检测', null, '只要温度适合就应使用超声波检测法检验注入点', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2183', '8', '压力管线', '71', '直管', null, '超声波检测', null, '对于这类系统（水沉积在盲管中，在低温的环境下会结冰将管子冻裂），就需要用超声波和射线等检测技术进行全面检验，找出发生露点腐蚀的区域', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2184', '8', '压力管线', '71', '直管', null, '超声波检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2185', '8', '压力管线', '71', '直管', null, '超声波检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2186', '8', '压力管线', '71', '直管', null, '超声波检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2187', '8', '压力管线', '71', '直管', null, '超声波检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', '0', 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2188', '8', '压力管线', '71', '直管', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行。(1)管道类别1：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为75%                                                                              ', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2189', '8', '压力管线', '71', '直管', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(2)管道类别2：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为50%                                                                                   ', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('2190', '8', '压力管线', '71', '直管', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(3)管道类别3：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为25%', '0', 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
