INSERT INTO `bh_knowled_standard` VALUES ('4278', '8', '压力管线', '72', '三通', null, '超声波检测', null, '即使外观检查法未检查到裂纹，仍应考虑使用湿荧光磁粉检测、磁粉检测、液体渗透或超声波检测等方法抽点检查。', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4279', '8', '压力管线', '72', '三通', null, '超声波检测', null, '液体渗透、荧光和超声波检测可以用于磁性和非磁性材料', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4280', '8', '压力管线', '72', '三通', null, '超声波检测', null, '检验内表面的应力腐蚀裂纹和碱脆裂纹，当不能使用磁粉、液体渗透或荧光检验的方法时，这种情况下，要用射线、超声波和利用涡流仪器进行检测，或采样进行显微检验。', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4281', '8', '压力管线', '72', '三通', null, '超声波检测', null, '疲劳断裂首先可以在应力集中的部位检测到，如管道分支的连接处', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
