INSERT INTO `bh_knowled_standard` VALUES ('4409', '8', '压力管线', '72', '三通', null, '射线检测', null, '采用射线探伤或内部检验检查焊道腐蚀情况', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4410', '8', '压力管线', '72', '三通', null, '射线检测', null, '如果在运行中检测到管道系统中存在裂纹状缺陷时，应采用射线探伤法及超声波探伤法进行进一步检验，评定缺陷等级，并努力确定裂纹缺陷是来自原始焊接还是环境开裂', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4411', '8', '压力管线', '72', '三通', null, '射线检测', null, '如可能或必要时，可采用定期使用射线及热红外成像检测管道内部结垢或堵塞情况。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4412', '8', '压力管线', '72', '三通', null, '射线检测', null, '超声波及射线检测可用来进行局部腐蚀的检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4413', '8', '压力管线', '72', '三通', null, '射线检测', null, '焊接接头，腐蚀经常会以凹坑形式分布在焊缝或其临近的热影响区，无论凹陷还是焊接缺陷都能用射线检测方法测出', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4414', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '疲劳断裂首先可以在应力集中的部位检测到，如管道分支的连接处', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4415', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '检验疲劳裂纹较好的无损检测方法包括渗透检测或磁粉检测、斜探头超声波检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4416', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行。(1)管道类别1：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为75%                                                                              ', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4417', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4418', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4419', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4420', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4421', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(2)管道类别2：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为50%                                                                                   ', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4422', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(3)管道类别3：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为25%', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('4423', '8', '压力管线', '72', '三通', null, '磁粉检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(1)管道类别1：在管道易受温度影响的范围内（垂直管道的绝热层终止部位）通过无损检测方法对以下管道系统怀疑部位的检验比例为50%：a)绝热层脱落或破损的部位，如：1)盲管部位（排气管、排水管和其他类似部位）', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
