INSERT INTO `bh_knowled_standard` VALUES ('5583', '8', '压力管线', '73', '弯头', null, '射线检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(3)管道类别3：在管道易受温度影响的范围内（已知绝热系统存在破裂的管道系统的低点，包括长且无支承管道的低点）通过无损检测方法对以下管道系统怀疑部位的检验比例为10%：i)高合金钢管系统中隔热层下的碳钢或低合金钢法兰、螺栓和其他组件', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5584', '8', '压力管线', '73', '弯头', null, '射线检测', null, '可根据绝热层下腐蚀检验的经验，增加或减少规定的绝热层下腐蚀的检验项目，不需要十分严格地按其规定进行检验。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5585', '8', '压力管线', '73', '弯头', null, '射线检测', null, '对于这类系统（水沉积在盲管中，在低温的环境下会结冰将管子冻裂），就需要用超声波和射线等检测技术进行全面检验，找出发生露点腐蚀的区域', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5586', '8', '压力管线', '73', '弯头', null, '射线检测', null, '检验内表面的应力腐蚀裂纹和碱脆裂纹，当不能使用磁粉、液体渗透或荧光检验的方法时，这种情况下，要用射线、超声波和利用涡流仪器进行检测，或采样进行显微检验。', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5587', '8', '压力管线', '73', '弯头', null, '射线检测', null, '为检查焊道腐蚀情况，采用射线探伤或内部检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5588', '8', '压力管线', '73', '弯头', null, '射线检测', null, '在某种情况下，射线探伤可以检查出焊缝中的缺陷', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5589', '8', '压力管线', '73', '弯头', null, '射线检测', null, '采用射线探伤或内部检验检查焊道腐蚀情况', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5590', '8', '压力管线', '73', '弯头', null, '射线检测', null, '如果在运行中检测到管道系统中存在裂纹状缺陷时，应采用射线探伤法及超声波探伤法进行进一步检验，评定缺陷等级，并努力确定裂纹缺陷是来自原始焊接还是环境开裂', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5591', '8', '压力管线', '73', '弯头', null, '射线检测', null, '如可能或必要时，可采用定期使用射线及热红外成像检测管道内部结垢或堵塞情况。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5592', '8', '压力管线', '73', '弯头', null, '射线检测', null, '超声波及射线检测可用来进行局部腐蚀的检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5593', '8', '压力管线', '73', '弯头', null, '射线检测', null, '焊接接头，腐蚀经常会以凹坑形式分布在焊缝或其临近的热影响区，无论凹陷还是焊接缺陷都能用射线检测方法测出', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5594', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '疲劳断裂首先可以在应力集中的部位检测到，如管道分支的连接处', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5595', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '检验疲劳裂纹较好的无损检测方法包括渗透检测或磁粉检测、斜探头超声波检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5596', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行。(1)管道类别1：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为75%                                                                              ', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5597', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5598', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5599', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5600', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
