INSERT INTO `bh_knowled_standard` VALUES ('5720', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '可根据绝热层下腐蚀检验的经验，增加或减少规定的绝热层下腐蚀的检验项目，不需要十分严格地按其规定进行检验。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5721', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '即使外观检查法未检查到裂纹，仍应考虑使用湿荧光磁粉检测、磁粉检测、液体渗透或超声波检测等方法抽点检查。', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5722', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '磁粉检测可以用于铁磁性材料。', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5723', '8', '压力管线', '73', '弯头', null, '磁粉检测', null, '应定期对振动管道的固定连接件采用磁粉探伤或渗透探伤检验是否出现疲劳缺陷，应特别注意支管连接。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5724', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '应定期对振动管道的固定连接件采用磁粉探伤或渗透探伤检验是否出现疲劳缺陷，应特别注意支管连接。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5725', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '疲劳断裂首先可以在应力集中的部位检测到，如管道分支的连接处', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5726', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '检验疲劳裂纹较好的无损检测方法包括渗透检测或磁粉检测、斜探头超声波检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5727', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5728', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5729', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
