INSERT INTO `bh_knowled_standard` VALUES ('5851', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '可根据绝热层下腐蚀检验的经验，增加或减少规定的绝热层下腐蚀的检验项目，不需要十分严格地按其规定进行检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5852', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '即使外观检查法未检查到裂纹，仍应考虑使用湿荧光磁粉检测、磁粉检测、液体渗透或超声波检测等方法抽点检查', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5853', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '液体渗透、荧光和超声波检测可以用于磁性和非磁性材料', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5854', '8', '压力管线', '73', '弯头', null, '渗透检测', null, '对于奥氏体材料的检验，只能用无氯或低氯液体渗透的方法', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5855', '8', '压力管线', '73', '弯头', null, '涡流检测', null, '检验内表面的应力腐蚀裂纹和碱脆裂纹，当不能使用磁粉、液体渗透或荧光检验的方法时，这种情况下，要用射线、超声波和利用涡流仪器进行检测，或采样进行显微检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5856', '8', '压力管线', '73', '弯头', null, '涡流检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5857', '8', '压力管线', '73', '弯头', null, '涡流检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5858', '8', '压力管线', '73', '弯头', null, '涡流检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5859', '8', '压力管线', '73', '弯头', null, '涡流检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5860', '8', '压力管线', '73', '弯头', null, '金相检验', null, '怀疑发生石墨化时，应从焊接接头处取样金相金相检验，以找出过量石墨化的证据', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5861', '8', '压力管线', '73', '弯头', null, '金相检验', null, '检验蠕变断裂的无损检测方法包括渗透检测、磁粉检测、超声波检测、射线检测和现场金相检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5862', '8', '压力管线', '73', '弯头', null, '声发射检测', null, '采用声发射检测、声波检漏检测仪和热红外成像检测的方法进行远距离泄漏检验或监测', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5863', '8', '压力管线', '73', '弯头', null, '声发射检测', null, '也可以在压力试验中采用声发射方法检测是否存在被压力或应力激活的裂纹', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5864', '8', '压力管线', '73', '弯头', null, '硬度检查', null, '自然冷却易硬化的合金钢在进行热处理后要检查其焊缝的硬度', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5865', '8', '压力管线', '73', '弯头', null, '硬度检查', null, '还应检查处于环境开裂中的碳钢的焊缝硬度', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5866', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '外观检验是确定管道、绝热层、防腐漆、涂层和与管道相连的构件的外表面状况，检查有无偏移、振动和泄漏', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5867', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '外部宏观检验用来确定管道外表面、绝热层、涂层、覆盖层以及相关金属构件的情况，并检查偏离、振动和泄漏的迹象', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5868', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '外部检验可在管道运行状态下进行', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5869', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '外部检验应包括管道支承和吊架的检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5870', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '腐蚀、冲蚀和结垢，将阀门、管件拆下或在法兰处拱起管子将其分开，以便外观检查', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5871', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '裂纹，除了强力组装焊道、焊缝附近的热影响区以及约束点和高应变区之外，焊缝包括角焊缝，易受到应力腐蚀开裂、氢损伤、碱脆或胺脆的位置，以及螺纹连接处的外露螺纹也要注意', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5872', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '焊接接头，若怀疑在焊道处发生腐蚀，检验方案中就应包括对焊道进行抽样检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5873', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '偏移度', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5874', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '振动：在操作中发现过度振动或摆动的地方，停车后应检查运行时无法查到的位置有无焊缝裂纹，并应检查其振动和外部摩擦情况', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5875', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '过热点：在操作过程中发现在装有内部隔热层的管道出现过热点的地方，停工后应对内部隔热层进行外观检查，看有无局部或完全脱落情况', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5876', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '应对过热点附近的管壁进行外观检查，看有无氧化和由此产生的氧化层', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5877', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '应清除全部氧化层，检查剩下的完好金属有无新出现的裂纹', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5878', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '若发现管道中一个区域有劣化，则应对该区域的上游和下游管道进行检验，还要检验相连的设备', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5879', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '若发现压力容器发生劣化，也应检验相连管道', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5880', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '复查绝热层的完好性，是否可以导致绝热层下腐蚀，绝热层下有无腐蚀迹象', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5881', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '应检查防腐衬里的脱落、破损、穿孔和鼓包情况', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5882', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '若发现衬里出现脱落、破损、穿孔或鼓包中任一情况，就有必要拆除部分内部衬里，检查衬里的耐腐蚀效力以及衬里下金属的表面状况', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5883', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '对于有耐火衬里类型的管道，应通过目测、红外线测量或温度指示涂料定期地进行温度监控，以确保衬里完好', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5884', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '如果发现耐火衬里出现脱离和鼓包，应拆除部分耐火衬里检查其下的管道', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5885', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5886', '8', '压力管线', '74', '变径头', null, '宏观检查', null, '当条件允许且实际需要时，可对大口径传输管道、排泄管道、催化剂管道或其他大口径管道等管道系统做内部外观检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5887', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5888', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '如果未发现耐火衬里出现脱离和鼓包，应在金属外表面进行超声测厚', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5889', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '当外观检查的区域中发现有不规则的腐蚀或冲蚀时，最好用射线检测或超声波测厚仪检验一下管道中看不见的区域', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5890', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '在定期检验中，应对注入点管段的上游305m(12in)区域和下游至少十倍管径区域进行全面的检验，并测量和记录所有注入点管段上的定点测厚区的测厚值', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5891', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：a)在注入点管段上选取适当附件作为定点测厚区', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5892', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：b)选取受注入流体冲击的位置作为定点测厚区', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5893', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：c)在注入点管段的长直管段上选取中间部位作为定点测厚区域', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5894', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '在产生局部腐蚀的注入点管段上选取定点测厚区时，应遵循以下原则：d)在注入点管段上选取上游和下游的两端位置作为定点测厚区', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5895', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '检验员应测量所选择盲管的厚度，包括滞流末端和它与相邻管道的连接处', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5896', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '检验注入点较好的方法为射线检测法及超声波检测法，要适当的确定出每个定点测厚区的最小厚度值', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5897', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '对于容易产生局部冲蚀/腐蚀的部位，应采用适当的无损检测方法进行大面积测厚，如超声波检测、射线检测或涡流检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5898', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '当管道公称直径大于等于25mm时，用超声波测厚仪测厚通常是最精确测厚的方法', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5899', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '射线测厚技术更适合于管道公称直径为25mm和更小的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5900', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '对于局部位置测量可以使用射线测厚技术，特别是对有绝热层的或可能有不均匀腐蚀、局部腐蚀的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5901', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '如果可行，可采用超声波测量需要记录部位的实际厚度', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5902', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '当管道系统的腐蚀不均匀或剩余壁厚接近最小允许壁厚时，则需要进行附加测厚。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5903', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '需要进行附加测厚时，采用射线或超声波扫描法更合适，也可使用涡流探伤设备', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5904', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：a) 发生泄漏事故后对安全和环境造成很大危害的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5905', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：b) 预计或经验证明腐蚀速率很高的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5906', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：c) 局部腐蚀严重的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5907', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：d) 结构上比较复杂的管道，如带有支管、盲管、连接件和其他类似的附件', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5908', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有以下特征的管道应选择较多的测厚点：e) 绝热层下严重腐蚀的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5909', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：a) 发生泄漏事故后对安全和环境危害不大的管道；', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5910', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：b) 相对无腐蚀的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5911', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有下列特征的管道，测厚点可减少：c) 长、直的管道。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5912', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有下列特征之一的管道系统．可以不用测厚：a) 在泄漏时，不危险或对环境影响极小的管道', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5913', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '具有下列特征之一的管道系统．可以不用测厚：b) 经资料或类似工况证实为不腐蚀及腐蚀程度不影响管道安全运行。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5914', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '每一个管道系统都应通过在测厚点进行厚度测量来监控', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5915', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '每个测厚点的最小壁厚值可用过超声波扫描或射线照相来确定', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5916', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '电磁技术也可用来确定减薄区域，然后用超声波或射线测量', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5917', '8', '压力管线', '74', '变径头', null, '厚度测定', null, '采用超声波测厚时，应在测厚位置多测几次以查找局部减薄，并应记录检测点的最小厚度值或几次测量值的平均值，然后计算管道的腐蚀速率、剩余寿命及下次检验日期。', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5918', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '在运行时管道表面出现沉积物的部位，如结焦部位，确定这些沉积物下是否存在严重的腐蚀是非常重要的。对于小口径管道，可以拆除选定双端法兰管，或者采用如射线检测法或外部超声检测法等无损检测方法对该区域进行检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5919', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '在运行时管道表面出现沉积物的部位，如结焦部位，确定这些沉积物下是否存在严重的腐蚀是非常重要的。对于大口径管道，应清除选定区域的沉积物进行坑蚀检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5920', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于某些类型的衬里，如爆炸结合的覆盖层或堆焊层，也可以选择在外表面使用超声波检测、测量壁厚和检查衬里是否脱落、破损和鼓包', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5921', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '只要温度适合就应使用超声波检测法检验注入点', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5922', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于这类系统（水沉积在盲管中，在低温的环境下会结冰将管子冻裂），就需要用超声波和射线等检测技术进行全面检验，找出发生露点腐蚀的区域', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5923', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5924', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5925', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5926', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5927', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行。(1)管道类别1：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为75%                                                                              ', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5928', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(2)管道类别2：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为50%                                                                                   ', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5929', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考表规定的检验范围进行。(3)管道类别3：用无损检测方法检验的大约面积或损坏绝热层的部位拆除绝热层的大约比例为25%', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5930', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(1)管道类别1：在管道易受温度影响的范围内（垂直管道的绝热层终止部位）通过无损检测方法对以下管道系统怀疑部位的检验比例为50%：a)绝热层脱落或破损的部位，如：1)盲管部位（排气管、排水管和其他类似部位）', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('5931', '8', '压力管线', '74', '变径头', null, '超声波检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(1)管道类别1：在管道易受温度影响的范围内（垂直管道的绝热层终止部位）通过无损检测方法对以下管道系统怀疑部位的检验比例为50%：a)绝热层脱落或破损的部位，如：2)管道的吊架和其他支架', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
