INSERT INTO `bh_knowled_standard` VALUES ('6438', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(3)管道类别3：在管道易受温度影响的范围内（已知绝热系统存在破裂的管道系统的低点，包括长且无支承管道的低点）通过无损检测方法对以下管道系统怀疑部位的检验比例为10%：g)绝热层或防腐系统的隆起、风化或是缺少箍带的部位', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6439', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(3)管道类别3：在管道易受温度影响的范围内（已知绝热系统存在破裂的管道系统的低点，包括长且无支承管道的低点）通过无损检测方法对以下管道系统怀疑部位的检验比例为10%：h)已知绝热系统存在破裂的管道系统的地点，包括长且无支承管道的低点', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6440', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '对于管道系统和没有做过绝热层下腐蚀检验的位置，应参考规定的检验范围进行：(3)管道类别3：在管道易受温度影响的范围内（已知绝热系统存在破裂的管道系统的低点，包括长且无支承管道的低点）通过无损检测方法对以下管道系统怀疑部位的检验比例为10%：i)高合金钢管系统中隔热层下的碳钢或低合金钢法兰、螺栓和其他组件', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6441', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '可根据绝热层下腐蚀检验的经验，增加或减少规定的绝热层下腐蚀的检验项目，不需要十分严格地按其规定进行检验', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6442', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '即使外观检查法未检查到裂纹，仍应考虑使用湿荧光磁粉检测、磁粉检测、液体渗透或超声波检测等方法抽点检查', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6443', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '液体渗透、荧光和超声波检测可以用于磁性和非磁性材料', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6444', '8', '压力管线', '74', '变径头', null, '渗透检测', null, '对于奥氏体材料的检验，只能用无氯或低氯液体渗透的方法', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6445', '8', '压力管线', '74', '变径头', null, '涡流检测', null, '检验内表面的应力腐蚀裂纹和碱脆裂纹，当不能使用磁粉、液体渗透或荧光检验的方法时，这种情况下，要用射线、超声波和利用涡流仪器进行检测，或采样进行显微检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6446', '8', '压力管线', '74', '变径头', null, '涡流检测', null, '当检验员怀疑或是有人提出某一特定管段容易产生环境开裂时，检验员应安排增补检验。这些检验可以选择表面无损检测（渗透检测或是湿性荧光磁粉检测）、超声波检测或是涡流无损检测', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6447', '8', '压力管线', '74', '变径头', null, '涡流检测', null, '如果可能，将此段（容易产生环境开裂的某一特定管段）的双端法兰管从管道系统中分离出来，剖开做内表面检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6448', '8', '压力管线', '74', '变径头', null, '涡流检测', null, '如果在压力容器的内部检验时发现了环境开裂，并且认为管道材料也同样敏感，检验员应指定对压力容器上游和下游的双端法兰管进行环境开裂检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6449', '8', '压力管线', '74', '变径头', null, '涡流检测', null, '当怀疑管段中存在潜在的环境开裂时，应选取即将转弯之前的双端法兰管', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6450', '8', '压力管线', '74', '变径头', null, '金相检验', null, '怀疑发生石墨化时，应从焊接接头处取样金相金相检验，以找出过量石墨化的证据', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6451', '8', '压力管线', '74', '变径头', null, '金相检验', null, '检验蠕变断裂的无损检测方法包括渗透检测、磁粉检测、超声波检测、射线检测和现场金相检验', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6452', '8', '压力管线', '74', '变径头', null, '声发射检测', null, '采用声发射检测、声波检漏检测仪和热红外成像检测的方法进行远距离泄漏检验或监测', null, 'SYT 6553-2003 管道检验规范 在用管道系统检验、修理、改造和再定级', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6453', '8', '压力管线', '74', '变径头', null, '声发射检测', null, '也可以在压力试验中采用声发射方法检测是否存在被压力或应力激活的裂纹', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6454', '8', '压力管线', '74', '变径头', null, '硬度检查', null, '自然冷却易硬化的合金钢在进行热处理后要检查其焊缝的硬度', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
INSERT INTO `bh_knowled_standard` VALUES ('6455', '8', '压力管线', '74', '变径头', null, '硬度检查', null, '还应检查处于环境开裂中的碳钢的焊缝硬度', null, 'SY/T 6635-2005管道系统组件检验推荐作法', null, null, null, null, null);
